import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { fetchPopularRepos } from '../utils/api';

function LanguagesNav({selected, onUpdateLanguage}) {
    const languages = ["All", "Ruby", "Javascript", "Java", "CSS", "Python"]

    return (
        <div>
            <ul className="flex-center">
                {languages.map(language =>
                <li key={language}>
                    <button 
                        onClick={() => onUpdateLanguage(language)} 
                        style={language === selected ? { color: 'maroon' } : null}
                        className="btn-clear nav-link">
                        {language}
                    </button>
                </li>
                )}
            </ul>
        </div>
    )
}

LanguagesNav.propTypes = {
    selected: PropTypes.string.isRequired,
    onUpdateLanguage: PropTypes.func.isRequired
}

class Popular extends Component {
    constructor(props) {
        super(props)

        this.state={
            selectedLanguage: "All",
            repos: {},
            error: null
        }
    this.updateLanguage = this.updateLanguage.bind(this)
    this.isLoading = this.isLoading.bind(this)
    }

    componentDidMount() {
        this.updateLanguage(this.state.selectedLanguage)
    }

    updateLanguage(selectedLanguage){
        this.setState({
            selectedLanguage,
            error: null,
        })

        if (!this.state.repos[selectedLanguage]) { //only fetch if you don't already have cached    
            fetchPopularRepos(selectedLanguage)
                .then((data) => { //set data as property on repos object
                    this.setState(({ repos }) => ({

                    }))
                })

            fetchPopularRepos(selectedLanguage)
                .then((repos) => this.setState({
                    repos,
                    error: null
                }))
                .catch(() => {
                    console.warn('Error fetching repos: ', this.error) //if error, warn the developer

                    this.setState({ //warn the user if error
                        error: 'There was an error fetching the repositories.'
                    })
                }) 
        }

    }

    isLoading() { //returns if there are no repos and no errors -- aka it's loading
        return this.state.repos === null && this.state.error === null
    }

    render() {
        const { selectedLanguage, repos, error } = this.state

        return(
            <React.Fragment>
                <LanguagesNav 
                    selected={selectedLanguage}
                    onUpdateLanguage={this.updateLanguage}
                />
                {this.isLoading() && <p>LOADING</p>}  {/*display LOADING if loading*/}

                {error && <p>{error}</p>}  {/*display error message if error*/}

                {repos && <pre>{JSON.stringify(repos, null, 2)}</pre>} {/*display repos object as string*/}
            </React.Fragment>
        )
    }
}



export default Popular;
