import React, {Component} from 'react';
import { FaUserFriends, FaFighterJet, FaTrophy, FaTiemsCircle, FaTimesCircle } from 'react-icons/fa';
import PropTypes from 'prop-types';
import Results from "./Results";

function Instructions() {
    return(
        <div className='instructions-container'>
            <h1 className='center-text header-lg'>
                Instructions
            </h1>
            <ol className='container-sm grid center-text battle-instructions'>
                <li>
                    <h3 className='header-sm'>
                        Enter two Github users
                    </h3>
                    <FaUserFriends className='bg-light' color='rgb(255, 191, 116' size={140} />
                </li>
                <li>
                    <h3 className='header-sm'>
                        Battle
                    </h3>
                    <FaFighterJet className='bg-light' color='#727272' size={140} />
                </li>
                <li>
                    <h3 className='header-sm'>
                        See the winners
                    </h3>
                    <FaTrophy className='bg-light' color='rgb(255, 215, 0)' size={140} />
                </li>
            </ol>
        </div>
    )
}

class PlayerInput extends Component {
    constructor(props) {
        super(props)

        this.state = {
            username: ""
        }
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleChange = this.handleChange.bind(this)
    }

    handleSubmit(event) {
        event.preventDefault()

        this.props.onSubmit(this.state.username)
    }

    handleChange(event) {
        this.setState({
            username: event.target.value
        })
    }


    render() {
        return(
            <form className='column player' onSubmit={this.handleSubmit}>
                <label htmlFor='username' className='player-label' >
                    {this.props.label}
                </label>
                <div className='row player-inputs' >
                    <input
                        type='text'
                        id='username'
                        className='input-light'
                        autoComplete='off'
                        value={this.state.username} //control the value in the component's state
                        onChange={this.handleChange} //when user inputs, handleChange updates state which then updates value
                    />
                    <button 
                        className='btn dark-btn'
                        type='submit'
                        disabled={!this.state.username}
                        >
                        Submit
                    </button>
                </div>
            </form>
        )
    }
}

PlayerInput.propTypes = {
    onSubmit: PropTypes.func.isRequired,
    label: PropTypes.string.isRequired
}


function PlayerPreview ({ username, onReset, label}) {
    return (
        <div className='column player'>
            <h3 className='player-label'>{label}</h3>
            <div className='row bg-light'>
                <div className='player-info'>
                    <img
                        className='avatar-sm'
                        src={`https://github.com/${username}.png?size=200`}
                        alt={`Avatar for ${username}`}
                    />
                    <a 
                        href={`https://github.com/${username}`}
                        className='link'>
                            {username}
                    </a>
                </div>
                <button className='btn-clear flex-center' onClick={onReset}>
                    <FaTimesCircle color='rgb(194, 57, 42)' size={26} />
                </button>
            </div>
        </div>
    )
}

PlayerPreview.propTypes = {
    username: PropTypes.string.isRequired,
    onReset: PropTypes.func.isRequired,
    label: PropTypes.string.isRequired
}


class Battle extends Component {
    constructor(props) {
        super(props)

        this.state = {
            playerOne: null,
            playerTwo: null,
            battle: false
        }
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleReset = this.handleReset.bind(this)
    }

    handleSubmit(id, player) {
        this.setState ({
            [id]: player
        })
    }

    handleReset(id) {
        this.setState({
            [id]: null
        })
    }

    render() {
        const { playerOne, playerTwo } = this.state

        return(
          <React.Fragment>
            <Instructions />

            <div className='players-container'>
                <h1 className='center-text header-lg'> Players </h1>
                <div className='row space-around'>
                    {playerOne === null //if we haven't already rendered player1, render it
                        ? <PlayerInput
                            label='Player One'
                            onSubmit={(player) => this.handleSubmit('playerOne', player)}
                        />
                        : <PlayerPreview //if we have rendered player1 show preview of player
                            username={playerOne} 
                            label='Player One' 
                            onReset={() => this.handleReset('playerOne')}
                        />
                    }  

                    {playerTwo === null //if we haven't already rendered player2, render it
                        ? <PlayerInput
                            label='Player Two'
                            onSubmit={(player) => this.handleSubmit('playerTwo', player)}
                        />
                        : <PlayerPreview //if we have rendered player2 show preview of player
                            username={playerTwo} 
                            label='Player Two' 
                            onReset={() => this.handleReset('playerTwo')}
                        />
                    }  
                </div>


                   {playerOne && playerTwo && (
                        <button 
                            className='btn dark-btn btn-space'
                            onClick={() => this.setState({battle: true})}>
                            Battle
                        </button>
                   )} 
            </div>
          </React.Fragment>  
        )
    }
}

export default Battle
